import { useState, useEffect } from 'react';

function useStats() {
  const [stats, useStats] = useState();
  useEffect(() => {
    async function fetchData() {
      console.log('fetching data');
      const data = fetch('https://covid19.mathdro.id/api').then(
        data => data.json
      );
    }
    fetchData();
  }, []);
}

function Stats() {
  const stats = useStats();
  console.log(stats);
  return (
    <div>
      {' '}
      <p> Stats: </p>
    </div>
  );
}

export default function IndexPage() {
  return (
    <div>
      <Stats />
    </div>
  );
}
